package JavaBasics;

public class Incremental {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a= 1;
		int b= a++;
		System.out.println(a);
		System.out.println(b);

		int x= 1;
		int y= ++x;

		System.out.println(x);
		System.out.println(y);
	}

}
