package JavaBasics;

public class LoopingConcept {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10;
		while (a<=20)
		{
			System.out.println(a);
			a =  a+1;
			//a++;
		}
		
		System.out.println("*******************");
		
		for (int b=10;b>=-10; b--)
		{
			System.out.println(b);
			
		}
		
		System.out.println("*******************");
		
		 int i=9;  
		    do{  
		        System.out.println(i);  
		    i++;  
		    }while(i<=10);
		
	}

}
