package JavaBasics;

public class StringCon {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 1;
		double b= 2.5;
		
		String x= "hello";
		String y = "World";

		System.out.println(a+b+x+y);
	System.out.println(x+a+b+x+y);
	System.out.println(x+y+(a+b));				
		
	}

}
